import{_ as e,c as l,ac as d,o as a}from"./chunks/framework.BUevxtdZ.js";const y=JSON.parse('{"title":"Команды в игре","description":"Команды на Кошкокрафте","frontmatter":{"description":"Команды на Кошкокрафте","icon":"material/note-plus"},"headers":[],"relativePath":"gameplay/unique/commands.md","filePath":"gameplay/unique/commands.md","lastUpdated":1732143786000}'),o={name:"gameplay/unique/commands.md"};function n(r,t,s,i,c,g){return a(),l("div",null,t[0]||(t[0]=[d('<h1 id="команды-в-игре" tabindex="-1">Команды в игре <a class="header-anchor" href="#команды-в-игре" aria-label="Permalink to &quot;Команды в игре&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">Важно</p><p><code>&lt; &gt;</code> - обязательное значение</p><p><code>( )</code> - необязательное</p></div><h2 id="плеисхолдеры" tabindex="-1">Плейсхолдеры <a class="header-anchor" href="#плеисхолдеры" aria-label="Permalink to &quot;Плейсхолдеры&quot;">​</a></h2><p><strong>Плейсхолдеры</strong> это текст, который заменяется на другой в чате!</p><p>Всё что нужно - ввести плейсхолдер в чат.</p><table tabindex="0"><thead><tr><th style="text-align:left;">Плейсхолдер</th><th style="text-align:left;">Назначение</th></tr></thead><tbody><tr><td style="text-align:left;"><code>[pos]</code></td><td style="text-align:left;">Показать в чат своё местоположение</td></tr><tr><td style="text-align:left;"><code>[ping]</code></td><td style="text-align:left;">Показать в чат свой пинг</td></tr><tr><td style="text-align:left;"><code>[item]</code></td><td style="text-align:left;">Показать в чат предмет в руке. Карты можно будет увидеть, книгу прочитать, а остальное - посмотреть. Не работает в ЛС!</td></tr><tr><td style="text-align:left;"><code>[inv]</code></td><td style="text-align:left;">Показать в чат свой инвентарь</td></tr><tr><td style="text-align:left;"><code>[ec]</code></td><td style="text-align:left;">Показать в чат свой Эндер-сундук</td></tr></tbody></table><h2 id="общение" tabindex="-1">Общение <a class="header-anchor" href="#общение" aria-label="Permalink to &quot;Общение&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">Команда</th><th style="text-align:left;">Назначение</th></tr></thead><tbody><tr><td style="text-align:left;"><code>!&lt;сообщение&gt;</code></td><td style="text-align:left;">Отправить сообщение в <strong>глобальный</strong> чат</td></tr><tr><td style="text-align:left;"><code>&lt;сообщение&gt;</code></td><td style="text-align:left;">Отправить сообщение в <strong>локальный</strong> чат (40 блоков вокруг тебя)</td></tr><tr><td style="text-align:left;"><code>/msg &lt;ник&gt; &lt;сообщение&gt; /tell &lt;ник&gt; &lt;сообщение&gt; /w &lt;ник&gt; &lt;сообщение&gt;</code></td><td style="text-align:left;">Отправить личное сообщение игроку</td></tr><tr><td style="text-align:left;"><code>/mail &lt;send/clear&gt; &lt;ник&gt; &lt;сообщение&gt;</code></td><td style="text-align:left;"><code>send</code> - отправить котику <strong>оффлайн</strong> сообщение. <code>clear</code> - очистит уже имеющиеся сообщения.</td></tr></tbody></table><h2 id="деиствия" tabindex="-1">Действия <a class="header-anchor" href="#деиствия" aria-label="Permalink to &quot;Действия&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">Команда</th><th style="text-align:left;">Назначение</th></tr></thead><tbody><tr><td style="text-align:left;"><code>/premium</code></td><td style="text-align:left;">Перевести свой аккаунт в режим лицензии - позволяет заходить на сервер без пароля. <br><span class="red bold">Если у тебя пиратка - не прописывай! Ты больше не попадёшь на сервер!</span></td></tr><tr><td style="text-align:left;"><code>/discord link</code></td><td style="text-align:left;">Привязать свой Майнкрафт аккаунт к аккаунту Discord</td></tr><tr><td style="text-align:left;"><code>/rp</code></td><td style="text-align:left;">Загрузить ресурспак сервера</td></tr><tr><td style="text-align:left;"><code>/afk</code></td><td style="text-align:left;">Встать в режим AFK. Перед ником появится префикс, и будет уведомление о том, что ты АФК</td></tr><tr><td style="text-align:left;"><code>/afkcheck &lt;ник&gt;</code></td><td style="text-align:left;">Проверить в AFK ли котик</td></tr><tr><td style="text-align:left;"><code>/kit (название)</code></td><td style="text-align:left;">Без названия - открыть меню китов. С названием - выдать себе кит.</td></tr><tr><td style="text-align:left;"><code>/tpa &lt;ник&gt;</code></td><td style="text-align:left;">Отправить запрос на телепортацию к котику. Принять запрос можно только если у тебя есть <strong>Котик+++</strong></td></tr><tr><td style="text-align:left;"><code>/wspeed &lt;скорость&gt;</code></td><td style="text-align:left;">Позволяет установить свою скорость. Число должно быть меньше 1, например <code>/wspeed 0.5</code></td></tr><tr><td style="text-align:left;"><code>/seen &lt;ник&gt;</code></td><td style="text-align:left;">Увидеть когда котик был последний раз на сервере</td></tr><tr><td style="text-align:left;"><code>/suicide</code></td><td style="text-align:left;">Позволяет покинуть этот мир</td></tr><tr><td style="text-align:left;"><code>/playtime (ник)</code></td><td style="text-align:left;">Покажет сколько ты наиграл. Если с ником - покажет сколько наиграл другой котик</td></tr><tr><td style="text-align:left;"><code>/playtimetop</code></td><td style="text-align:left;">Посмотреть топ по наигранным часам</td></tr><tr><td style="text-align:left;"><code>/itemframe</code></td><td style="text-align:left;">Включить настройку рамки</td></tr><tr><td style="text-align:left;"><code>/help</code></td><td style="text-align:left;">Получить этот список команд внутри игры</td></tr><tr><td style="text-align:left;"><code>/findnick &lt;примерный ник&gt;</code></td><td style="text-align:left;">Поиск ника котика по примерному<br><code>/findnick Szar</code> -&gt; <code>Нашёл примерный ник - Szarkan</code></td></tr><tr><td style="text-align:left;"><code>/qmenu</code></td><td style="text-align:left;">Открыть меню текущих квестов</td></tr><tr><td style="text-align:left;"><code>/eggowner</code></td><td style="text-align:left;">Посмотреть кто сейчас владеет <a href="./dragon_egg">Яйцом Дракона</a></td></tr></tbody></table><h2 id="экономика" tabindex="-1">Экономика <a class="header-anchor" href="#экономика" aria-label="Permalink to &quot;Экономика&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">Команда</th><th style="text-align:left;">Назначение</th></tr></thead><tbody><tr><td style="text-align:left;"><code>/balance (ник)</code></td><td style="text-align:left;">Проверить твой или чужой баланс АРов</td></tr><tr><td style="text-align:left;"><code>/pay &lt;ник&gt; &lt;сумма&gt;</code></td><td style="text-align:left;">Перевести котику АРы с эл. счёта</td></tr><tr><td style="text-align:left;"><code>/cheque &lt;сумма&gt;</code></td><td style="text-align:left;">Выписать бумажный чек. Обналичить чек можно нажав <code>ПКМ</code> с ним в руке</td></tr><tr><td style="text-align:left;"><code>/baltop</code></td><td style="text-align:left;">Топ богачей по АРам</td></tr></tbody></table><h2 id="защита-территории" tabindex="-1">Защита территории <a class="header-anchor" href="#защита-территории" aria-label="Permalink to &quot;Защита территории&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">Команда</th><th style="text-align:left;">Назначение</th></tr></thead><tbody><tr><td style="text-align:left;"><code>/pt info</code></td><td style="text-align:left;">Информация по региону, в котором ты стоишь</td></tr><tr><td style="text-align:left;"><code>/pt list</code></td><td style="text-align:left;">Список твоих регионов</td></tr><tr><td style="text-align:left;"><code>/pt name &lt;название&gt;</code></td><td style="text-align:left;">Изменить название твоего региона, в котором ты стоишь</td></tr><tr><td style="text-align:left;"><code>/pt add/remove &lt;ник&gt;</code></td><td style="text-align:left;">Добавить/удалить частника твоего региона. Учатники могут ломать, строить, залезать в сундуки, но не менять настройки региона.</td></tr><tr><td style="text-align:left;"><code>/pt addowner/removeowner &lt;ник&gt;</code></td><td style="text-align:left;">Добавить/удалить владельца в регионе. Владельцы могут менять регион как захотят - хоть удалить его. <span class="red"><strong>Поэтому подумай, прежде добавлять кого-то в свой регион владельцем!</strong></span></td></tr><tr><td style="text-align:left;"><code>/pt flag &lt;флаг&gt; &lt;allow/deny/значение&gt;</code></td><td style="text-align:left;">Изменить флаг региона. О них подробнее <a href="./land_protection#настройка-территории">здесь</a>.</td></tr><tr><td style="text-align:left;"><code>/pt unclaim</code></td><td style="text-align:left;">Удаляет твой регион, в котором ты стоишь. Все блоки защиты возвращаются в инвентарь</td></tr><tr><td style="text-align:left;"><code>/pt sell &lt;цена&gt;</code></td><td style="text-align:left;">Выставить на продажу твой регион в котором ты стоишь</td></tr><tr><td style="text-align:left;"><code>/pt buy</code></td><td style="text-align:left;">Купить регион в котором ты стоишь (если он выставлен на продажу)</td></tr></tbody></table><h2 id="рп-команды" tabindex="-1">РП Команды <a class="header-anchor" href="#рп-команды" aria-label="Permalink to &quot;РП Команды&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">Команда</th><th style="text-align:left;">Назначение</th></tr></thead><tbody><tr><td style="text-align:left;">`/police &lt;wantedlist</td><td style="text-align:left;">warns</td></tr><tr><td style="text-align:left;"><code>/me &lt;действие&gt;</code></td><td style="text-align:left;">Описать действие, совершенное твоим персонажем<br><br><code>/me Погладил котика</code> -&gt; <code>*Szarkan погладил котика</code></td></tr><tr><td style="text-align:left;"><code>/do &lt;описание&gt;</code></td><td style="text-align:left;">Описать своего персонажа от 3-го лица<br><br><code>/do в зубах сигарета</code> -&gt; <code>*В зубах сигарета (Szarkan)</code></td></tr><tr><td style="text-align:left;"><code>/roll &lt;число&gt;</code></td><td style="text-align:left;">Получить случайное число от 1 до твоего выбранного.<br><br><code>/roll 100</code> -&gt; <code>*Szarkan рольнул 12 из 100</code></td></tr><tr><td style="text-align:left;"><code>/hidenicks</code></td><td style="text-align:left;">Скрыть у себя ники котиков</td></tr><tr><td style="text-align:left;"><code>/coinflip</code></td><td style="text-align:left;">Подкинуть монетку - выпадет орёл или решка</td></tr></tbody></table><h2 id="скины" tabindex="-1">Скины <a class="header-anchor" href="#скины" aria-label="Permalink to &quot;Скины&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">Команда</th><th style="text-align:left;">Назначение</th></tr></thead><tbody><tr><td style="text-align:left;"><code>/skin set &lt;ник&gt;</code></td><td style="text-align:left;">Установить себе скин по <strong>нику</strong></td></tr><tr><td style="text-align:left;"><code>/skin url &lt;ссылка&gt;</code></td><td style="text-align:left;">Установить скин по <strong>ссылке</strong></td></tr><tr><td style="text-align:left;"><code>/skin update</code></td><td style="text-align:left;">Обновить свой скин</td></tr><tr><td style="text-align:left;"><code>/skin clear</code></td><td style="text-align:left;">Убрать свой скин</td></tr><tr><td style="text-align:left;"><code>/sw &lt;ник/ссылка на скин&gt; &lt;комментарий&gt;</code></td><td style="text-align:left;">Добавить скин в меню <a href="./../unique/qol#_8">быстрой смены скина</a></td></tr></tbody></table><h2 id="команды-только-для-котиков" tabindex="-1"><strong>Команды только для</strong> <span class="neon">Котиков+</span> <a class="header-anchor" href="#команды-только-для-котиков" aria-label="Permalink to &quot;**Команды только для** &lt;span class=&quot;neon&quot;&gt;Котиков+&lt;/span&gt;&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">Команда</th><th style="text-align:left;">Назначение</th></tr></thead><tbody><tr><td style="text-align:left;"><code>/sethome (название)</code></td><td style="text-align:left;">Поставить точку дома</td></tr><tr><td style="text-align:left;"><code>/spawn</code></td><td style="text-align:left;">Телепортироваться на спавн</td></tr><tr><td style="text-align:left;"><code>/home (название)</code></td><td style="text-align:left;">Телепортироваться на точку дома. Без названия откроется меню домов</td></tr><tr><td style="text-align:left;"><code>/repair</code></td><td style="text-align:left;">Починить предмет в руке</td></tr><tr><td style="text-align:left;"><code>/homes</code></td><td style="text-align:left;">Список домов</td></tr><tr><td style="text-align:left;"><code>/heal</code> <code>/feed</code></td><td style="text-align:left;">Исцелить или накормить себя</td></tr><tr><td style="text-align:left;"><code>/itemname &lt;новое название&gt;</code></td><td style="text-align:left;">Поменять название предмета в руке</td></tr><tr><td style="text-align:left;"><code>/hat</code></td><td style="text-align:left;">Надеть предмет в руке на голову</td></tr><tr><td style="text-align:left;"><code>/wb</code></td><td style="text-align:left;">Открыть виртуальный верстак</td></tr><tr><td style="text-align:left;"><code>/anvil</code></td><td style="text-align:left;">Открыть виртуальную наковальню</td></tr><tr><td style="text-align:left;"><code>/loom</code></td><td style="text-align:left;">Открыть виртуальный ткацкий станок</td></tr><tr><td style="text-align:left;"><code>/smithingtable</code></td><td style="text-align:left;">Открыть виртуальный кузнечный стол</td></tr><tr><td style="text-align:left;"><code>/ec</code></td><td style="text-align:left;">Открыть виртуальный эндер-сундук</td></tr><tr><td style="text-align:left;"><code>/back</code></td><td style="text-align:left;">Вернуться на место телепортации</td></tr><tr><td style="text-align:left;"><code>/dback</code></td><td style="text-align:left;">Вернуться на место смерти</td></tr><tr><td style="text-align:left;"><code>/tpaaccept</code></td><td style="text-align:left;">Принять запрос на телепортацию</td></tr></tbody></table>',20)]))}const x=e(o,[["render",n]]);export{y as __pageData,x as default};
