import{_ as a,c as t,ac as o,o as i}from"./chunks/framework.BUevxtdZ.js";const h=JSON.parse('{"title":"Войсчат и аддоны","description":"Войсчат на Кошкокрафте","frontmatter":{"icon":"material/account-voice","title":"Войсчат и аддоны","description":"Войсчат на Кошкокрафте"},"headers":[],"relativePath":"gameplay/unique/voicechat.md","filePath":"gameplay/unique/voicechat.md","lastUpdated":1732143786000}'),l={name:"gameplay/unique/voicechat.md"};function p(r,e,c,s,n,d){return i(),t("div",null,e[0]||(e[0]=[o('<h1 id="воисчат-и-кастомные-пластинки" tabindex="-1">Войсчат и кастомные пластинки <a class="header-anchor" href="#воисчат-и-кастомные-пластинки" aria-label="Permalink to &quot;Войсчат и кастомные пластинки&quot;">​</a></h1><p>На сервере установлен войсчат <strong>PlasmoVoice</strong> вместе с несколькими аддонами!</p><p>Войсчат позволяет разговаривать и слышать других внутри игры!</p><div class="tip custom-block"><p class="custom-block-title">Установка мода</p><p><a href="https://modrinth.com/plugin/plasmo-voice/versions?l=forge&amp;l=fabric&amp;g=1.21.1" target="_blank" rel="noreferrer">Forge, Fabric, Quilt, NeoForge</a></p></div><h2 id="управление-воисчатом" tabindex="-1">Управление войсчатом <a class="header-anchor" href="#управление-воисчатом" aria-label="Permalink to &quot;Управление войсчатом&quot;">​</a></h2><p>Настройки мода можно посмотреть клавишей <code>V</code>, их там очень много! От порога микрофона и громкости отдельных игроков, до пространственного звучания и угла направления источников!</p><p>Изменять громкость типов голосовых или отдельных игроков можно во вкладке Громкость.</p><p>Разговаривать на <code>Left Alt</code></p><p><img src="/assets/gameplay/unique/voicechat/settings.png" alt="Настройки PlasmoVoice" loading="lazy"></p><h2 id="голосовые-группы" tabindex="-1">Голосовые группы <a class="header-anchor" href="#голосовые-группы" aria-label="Permalink to &quot;Голосовые группы&quot;">​</a></h2><p>Ты можешь создать приватные голосовые группы, чтобы общаться с друзьями на любом расстоянии!</p><p>Для этого введи команду <code>/groups create name:&lt;название&gt; password:&lt;пароль&gt;</code></p><p>Для выхода из группы вводи <code>/groups leave</code></p><h2 id="кастомные-пластинки" tabindex="-1">Кастомные пластинки <a class="header-anchor" href="#кастомные-пластинки" aria-label="Permalink to &quot;Кастомные пластинки&quot;">​</a></h2><p>На сервере можно создать свою пластинку с любой музыкой!</p><p>Для этого достаточно иметь 32 АРа и любую пластинку.</p><ol><li><p>Приходишь на спавн</p></li><li><p>Находишь NPC <strong>Музыкант</strong></p></li><li><p>Обращаешься к NPC и покупаешь доступ</p></li><li><p>Получаешь команду <code>/disc burn &lt;ссылка&gt; &lt;название&gt;</code> на 10 использований</p></li><li><p>Прописываешь эту команду, держа в руке пластинку</p></li><li><p>???</p></li><li><p>ПРОФИТ!</p></li></ol><p>Поддерживаются ссылки из Soundcloud, YouTube (и трансляции YouTube), трансляции Twitch, прямые <strong>HTTP</strong> ссылки и Bandcamp.</p>',18)]))}const m=a(l,[["render",p]]);export{h as __pageData,m as default};
