import{_ as t,c as a,ac as l,o}from"./chunks/framework.BUevxtdZ.js";const h=JSON.parse('{"title":"Ультимативный гайд по ГКСБ","description":"","frontmatter":{"hide":["navigation","toc","footer","path"],"search":{"exclude":true}},"headers":[],"relativePath":"guides/work/gksb.md","filePath":"guides/work/gksb.md","lastUpdated":1732143786000}'),i={name:"guides/work/gksb.md"};function p(r,e,n,s,d,c){return o(),a("div",null,e[0]||(e[0]=[l('<h1 id="ультимативныи-гаид-по-гксб" tabindex="-1">Ультимативный гайд по ГКСБ <a class="header-anchor" href="#ультимативныи-гаид-по-гксб" aria-label="Permalink to &quot;Ультимативный гайд по ГКСБ&quot;">​</a></h1><p>Привет, ГКСБ! Или не ГКСБ... Если ты не ГКСБ, то закрой страничку - тебе тут ловить нечего.</p><p>Итак. Ты - Глава Кошачьей Службы Безопасности. Важная роль!</p><h2 id="твои-обязанности" tabindex="-1">Твои обязанности: <a class="header-anchor" href="#твои-обязанности" aria-label="Permalink to &quot;Твои обязанности:&quot;">​</a></h2><ul><li><p>Ты - Лидер всех Офицеров! От тебя зависит то, как они работают и как исполняют свой священный долг!</p></li><li><p>Знать и понимать все законы и дыры в нём.</p></li><li><p>Обсуждать, допонять, улучшать законы нашей страны.</p></li><li><p>Уметь руководить сотрудниками, понимать что они хотят, понимать как это исправить.</p></li><li><p>Быть общительным, добрым и терпеливым - ты само лицо правосудия!</p><ul><li>А то были у нас ГКСБ - убивали своих же сотрудников и материли граждан...</li></ul></li><li><p>Стараться развивать коммуникацию жителей - ты не просто голова, нанимающая Офицеров, ты часть этой страны!</p></li></ul><p>Должность ГКСБ - важная должность. От тебя в частности зависит РП сервера.</p><p>Закроет ли Офицер репорт, попросив Изгнания у Всекотца, или устроят ли Офицеры настоящий рейд по гражданину - зависит от тебя.</p><h2 id="твои-доступные-команды" tabindex="-1">Твои доступные команды <a class="header-anchor" href="#твои-доступные-команды" aria-label="Permalink to &quot;Твои доступные команды&quot;">​</a></h2><p>Большинство остального по своей работе ты можешь найти в <a href="./police">Ультимативном гайде Офицера КСБ</a></p><p>??? tip &quot;Основные команды именно для ГКСБ&quot;</p><pre><code>- `/police add &lt;ник&gt;` - принять игрока на должность Офицера КСБ\n\n- `/police remove &lt;ник&gt;` - уволить сотрудника КСБ\n\n- `/police warn &lt;ник офицера&gt; &lt;причина&gt;` - выдать выговор сотруднику с причиной. Выговоры удаляются автоматически через 14 дней. \n\n- `/police wanted &lt;ник&gt;` - объявить гражданина в Розыск. У него над башкой и в ТАБе будет плашка &lt;span class=&quot;red shadow&quot;&gt;Розыск&lt;/span&gt;.\n\n- `/police unwated &lt;ник&gt;` - снять розыск с гражданина.\n</code></pre>',11)]))}const g=t(i,[["render",p]]);export{h as __pageData,g as default};
