import{_ as e,c as n,ac as o,j as s,a as t,G as i,w as p,B as l,o as d}from"./chunks/framework.BUevxtdZ.js";const x=JSON.parse('{"title":"Частые Вопросы","description":"Ответы на частые вопросы по Кошкокрафту","frontmatter":{"icon":"octicons/question-16","description":"Ответы на частые вопросы по Кошкокрафту"},"headers":[],"relativePath":"info/faq.md","filePath":"info/faq.md","lastUpdated":1732143786000}'),h={name:"info/faq.md"};function c(u,a,g,f,q,b){const r=l("Spoiler");return d(),n("div",null,[a[3]||(a[3]=o('<h1 id="частые-вопросы" tabindex="-1">Частые Вопросы <a class="header-anchor" href="#частые-вопросы" aria-label="Permalink to &quot;Частые Вопросы&quot;">​</a></h1><h2 id="какая-версия-сервера-пиратка-или-лицензия" tabindex="-1">Какая версия сервера? Пиратка или лицензия? <a class="header-anchor" href="#какая-версия-сервера-пиратка-или-лицензия" aria-label="Permalink to &quot;Какая версия сервера? Пиратка или лицензия?&quot;">​</a></h2><p>На момент <strong>6</strong> сезона - <strong>1.21.1 Java</strong>. Игра на Bedrock или PE издании, на данный момент, невозможна и не планируется.</p><p>Играть можно как с пиратки, так и с лицензии!</p><h2 id="ip" tabindex="-1">IP? <a class="header-anchor" href="#ip" aria-label="Permalink to &quot;IP?&quot;">​</a></h2><p><code>play.catcraftmc.ru</code> - основной</p><p><code>play.catcraftmc.xyz</code> - запасной</p><p><code>play.catcraft.ru</code> - запасной</p><h2 id="когда-был-ваип-когда-будет-следующии" tabindex="-1">Когда был вайп? Когда будет следующий? <a class="header-anchor" href="#когда-был-ваип-когда-будет-следующии" aria-label="Permalink to &quot;Когда был вайп? Когда будет следующий?&quot;">​</a></h2><p>Перед тем, как узнать дату вайпа, хотим тебе рассказать:</p><ul><li><p>Если ты волнуешься, что тебе не достанется лута - ванильные сундуки восстанавливаются раз в две недели</p></li><li><p>Если ты волнуешься, что все структуры залутаны - они восстанавливаются время от времени админами</p></li><li><p>Если ты волнуешься, что все игроки в топовых шмотках - аналогично будет и через неделю после вайпа</p></li></ul><p>Поэтому не стоит сильно заострять внимание на дате вайпа =)</p>',12)),s("p",null,[a[1]||(a[1]=t("Вайп был ")),i(r,null,{default:p(()=>a[0]||(a[0]=[t("13 июля 2024")])),_:1}),a[2]||(a[2]=t(". Дата следующего вайпа неизвестна никому."))]),a[4]||(a[4]=o('<h2 id="какие-размеры-миров" tabindex="-1">Какие размеры миров? <a class="header-anchor" href="#какие-размеры-миров" aria-label="Permalink to &quot;Какие размеры миров?&quot;">​</a></h2><p>Обычный мир - <strong>7500 х 75000</strong>, в скором времени будет расширение до <strong>15000</strong></p><p>Ад - <strong>5000 х 5000</strong></p><p>Энд - <strong>3250 x 3250</strong>, в скором времени будет расширение до <strong>15000</strong></p><h2 id="а-что-если-меня-ограбят-или-загриферят" tabindex="-1">А что, если меня ограбят или загриферят? <a class="header-anchor" href="#а-что-если-меня-ограбят-или-загриферят" aria-label="Permalink to &quot;А что, если меня ограбят или загриферят?&quot;">​</a></h2><p>Наши доблестные <strong>Офицеры КСБ</strong> помогут выявить нарушителя и он получит по заслугам!</p><p>Всё, что тебе нужно - отправить жалобу в нашем Дискорде. Полиция найдёт нарушителя и заставит выплатить тебе штраф или вернуть все вещи.</p><h2 id="какая-валюта-сервера" tabindex="-1">Какая валюта сервера? <a class="header-anchor" href="#какая-валюта-сервера" aria-label="Permalink to &quot;Какая валюта сервера?&quot;">​</a></h2><p><strong>АРы</strong> - <strong>глубиносланцевая</strong> алмазная руда!</p><h2 id="а-где-мне-можно-строиться" tabindex="-1">А где мне можно строиться? <a class="header-anchor" href="#а-где-мне-можно-строиться" aria-label="Permalink to &quot;А где мне можно строиться?&quot;">​</a></h2><p>Везде, кроме Спавна!</p><p>Определение территории спавна прописано в наших законах, <a href="./rules/laws">вот здесь</a>.</p><h2 id="как-создать-клан-город-опг" tabindex="-1">Как создать клан/город/опг? <a class="header-anchor" href="#как-создать-клан-город-опг" aria-label="Permalink to &quot;Как создать клан/город/опг?&quot;">​</a></h2><p>По сути - берёшь и создаёшь! Набираешь себе людей, РПшишь или делаешь что хочешь и всё.</p><p>Но если хочешь более официально, то клан нужно купить - подробнее об этом <a href="./../gameplay/unique/clans">по этой ссылке</a>.</p><h2 id="почему-кошкокрафт-вы-няняня-сервер-для-детеи" tabindex="-1">Почему &#39;&#39;Кошкокрафт&#39;&#39;? Вы &#39;&#39;няняня&#39;&#39; сервер для детей? <a class="header-anchor" href="#почему-кошкокрафт-вы-няняня-сервер-для-детеи" aria-label="Permalink to &quot;Почему &#39;&#39;Кошкокрафт&#39;&#39;? Вы &#39;&#39;няняня&#39;&#39; сервер для детей?&quot;">​</a></h2><p>У этого названия есть целая история! Подробнее <a href="/history/1season">по этой ссылке =)</a></p><p>Но нет - мы не &quot;сервер для детей&quot;. Мы сервер для <strong>всех</strong>!</p><h2 id="почему-мне-запрещено-ставить-тнт-кристаллы-энда-и-спавнить-визера-в-первые-10-часов-игры" tabindex="-1">Почему мне запрещено ставить ТНТ, Кристаллы Энда и спавнить визера в первые 10 часов игры? <a class="header-anchor" href="#почему-мне-запрещено-ставить-тнт-кристаллы-энда-и-спавнить-визера-в-первые-10-часов-игры" aria-label="Permalink to &quot;Почему мне запрещено ставить ТНТ, Кристаллы Энда и спавнить визера в первые 10 часов игры?&quot;">​</a></h2><p>Потому что многие гриферы могут зайти, быстренько найти где-то динамит, что-то взорвать и уйти с сервера.</p><p>Дабы не дать им такого наслаждения, им нужно наиграть минимум 10 часов.</p><h2 id="что-за-версии-сервера-что-означает-6-x-x" tabindex="-1">Что за версии сервера? Что означает 6.x.x? <a class="header-anchor" href="#что-за-версии-сервера-что-означает-6-x-x" aria-label="Permalink to &quot;Что за версии сервера? Что означает 6.x.x?&quot;">​</a></h2><p>Первая цифра - <strong>текущий сезон</strong>,</p><p>Вторая цифра - <strong>глобальные/большие</strong> обновления,</p><p>Третья цифра - <strong>минорные</strong> патчи/обновления/фиксы,</p><p>В редких случаях четвертая цифра - <strong>хотфиксы</strong> или <strong>маленькие</strong> патчи.</p><p>Тоесть <code>6.2.1.1</code> будет означать <strong>6</strong> сезон, <strong>2</strong> глобальных обновления, <strong>1</strong> маленькое обновление и <strong>1</strong> хот-фикс.</p><h2 id="что-за-сообщения-github-в-канале-разработка-сервера" tabindex="-1">Что за сообщения GitHub в канале <code>#разработка-сервера</code>? <a class="header-anchor" href="#что-за-сообщения-github-в-канале-разработка-сервера" aria-label="Permalink to &quot;Что за сообщения GitHub в канале `#разработка-сервера`?&quot;">​</a></h2><p>Это сообщения о том, что кто-то из админов что-то добавил на сервер разработки - локальный админский сервер для создания и тестирования обновлений.</p><p>Например, на картинке ниже написано <code>&quot;Кастомные звуки теперь имеют субтитры&quot;</code>. Это означает то, что кто-то из админов добавил такую фишку на сервер разработки и скоро она появится на основном сервере!</p><p>Ссылки в таких сообщениях открывать нельзя, потому что файлы Кошкокрафта - приватные =)</p><p><img src="/assets/info/faq/github_catcraft.png" alt="Сообщения GitHub на Кошкокрафте" loading="lazy"></p>',32))])}const P=e(h,[["render",c]]);export{x as __pageData,P as default};
