import{_ as i,c as a,ac as e,o as t}from"./chunks/framework.BUevxtdZ.js";const p=JSON.parse('{"title":"6.2.5","description":"Обновление 6.2.5, добавляющее новую систему кланов, много изменений NPC и кучу баг-фиксов!","frontmatter":{"title":"6.2.5","date":"2024-11-17T00:00:00.000Z","authors":["szarkan"],"slug":"6_2_5","description":"Обновление 6.2.5, добавляющее новую систему кланов, много изменений NPC и кучу баг-фиксов!","categories":["Патч"]},"headers":[],"relativePath":"updates/6season/6_2_5.md","filePath":"updates/6season/6_2_5.md","lastUpdated":1732143786000}'),o={name:"updates/6season/6_2_5.md"};function r(s,l,n,d,u,c){return t(),a("div",null,l[0]||(l[0]=[e('<h1 id="новые-кланы-изменения-npc-фиксы-багов-6-2-5" tabindex="-1">Новые кланы, изменения NPC, фиксы багов - 6.2.5 <a class="header-anchor" href="#новые-кланы-изменения-npc-фиксы-багов-6-2-5" aria-label="Permalink to &quot;Новые кланы, изменения NPC, фиксы багов - 6.2.5&quot;">​</a></h1><ul><li><p>Абсолютно новая система кланов и повышений уровней!</p></li><li><p>Новые реплики NPC</p></li><li><p>Различные изменения для новичков</p></li><li><p>Различные QoL улучшения</p></li><li><p>Куча баг-фиксов</p></li></ul><p><img src="/assets/updates/6_2_5/preview.png" alt="Preview" loading="lazy"></p><h1 id="добавлено" tabindex="-1">Добавлено <a class="header-anchor" href="#добавлено" aria-label="Permalink to &quot;Добавлено&quot;">​</a></h1><h2 id="новая-система-кланов" tabindex="-1">Новая система кланов! <a class="header-anchor" href="#новая-система-кланов" aria-label="Permalink to &quot;Новая система кланов!&quot;">​</a></h2><p>Система кланов была полностью переделана, как и уровни, команды и всё-всё-всё, связанное с кланами!</p><p>Обо всём этом можно также прочитать на нашей Вики - [тут ссылка на страницу с кланами, если я конечно не забуду её вставить как ебаный дебил]</p><h3 id="но-перед-этим" tabindex="-1">Но перед этим… <a class="header-anchor" href="#но-перед-этим" aria-label="Permalink to &quot;Но перед этим…&quot;">​</a></h3><p>Данная система будет находится в <strong>Бете</strong>, и многое в ней может поменяться. Если у тебя есть идеи для системы уровней, тебе не нравится какая-то менюшка, какие-то команды - смело пиши нам в баг-репорт или в предложить-идею!</p><h3 id="новое-все-в-кланах" tabindex="-1">Новое всё в кланах <a class="header-anchor" href="#новое-все-в-кланах" aria-label="Permalink to &quot;Новое всё в кланах&quot;">​</a></h3><p>Теперь б<strong>о</strong>льшинство процессов в клане (настройка, приглашение игроков) может происходить в менюшках! Все команды были проверены, менюшки (по нашему мнению) удобно настроены.</p><h3 id="новая-система-уровнеи" tabindex="-1">Новая система уровней <a class="header-anchor" href="#новая-система-уровнеи" aria-label="Permalink to &quot;Новая система уровней&quot;">​</a></h3><p>Теперь есть два понятия - <strong>Уровень</strong> и <strong>Этап.</strong> Уровень это то, что вы получаете, набрав нужное количество очков, Этап - привычные нам названия “Клан”, “Гильдия” и т.п., которые даются за определенный уровень.</p><p>Сейчас Этапы выдаются вот так:</p><ul><li>0 уровень - Поселение <ul><li>Ни общаков, ни точек дома не доступно</li></ul></li><li>10 уровень - Братство <ul><li><ul><li>10 слотов участников</li></ul></li><li>Нужно иметь минимум 5 участников и знамя клана</li></ul></li><li>20 уровень - Клан <ul><li><ul><li>1 точка дома, + 1 общак (сундук) клана</li></ul></li><li>Нужно иметь 15 участников</li></ul></li><li>30 уровень - Гильдия <ul><li>Префиксы перед ником</li><li>Нужно иметь 20 участников</li></ul></li><li>50 уровень - Орден <ul><li>Отметка территории клана на карте, +1 точка дома</li><li>Нужно иметь 25 участников</li></ul></li><li>100 уровень - Государство <ul><li>Возможность отделиться от государства Кошкокрафта, создать свои законы, правила и т.п.</li><li>Нужно иметь 50 участников</li></ul></li></ul><p>Получать очки клана можно пока что лишь двумя способами:</p><ul><li>Принимать больше игроков в клан</li><li>Покупать опыт у Администратора за АРы</li></ul><h3 id="прочее-про-кланы" tabindex="-1">Прочее про кланы <a class="header-anchor" href="#прочее-про-кланы" aria-label="Permalink to &quot;Прочее про кланы&quot;">​</a></h3><ul><li>Кланам можно устанавливать любой Флаг с любым узором!</li><li>У кланов также есть 4 роли - гость, участник, зам-главы и глава, и всем им можно настраивать разрешения на отдельные действия.</li><li>Префикс и название кланов объединено в одно понятие - Тег клана. Подробнее о своём клане можно рассказать в его <strong>описании</strong>.</li><li>Неактивные кланы удаляются через 120 дней</li><li>Многие действия с кланами (создание, расформирование) отправляется в Информатора</li><li>У плагина 3 ТЫСЯЧИ строк перевода, и скорее всего мы могли где-то упустить кривой перевод/ошибки/английский язык. Милости просим залутать халявные АРы, сообщив об этом в баг-репорт =)</li></ul><h3 id="прочее" tabindex="-1">Прочее <a class="header-anchor" href="#прочее" aria-label="Permalink to &quot;Прочее&quot;">​</a></h3><ul><li>Жители Столицы могут прокомментировать недавно выпавший снег в [Общение]</li><li>У Музыкантки появились реплики Общения!</li><li>Закрамбола, Рыба-облако и Одуванчиковое вино возвращены!</li><li>Добавлен новый способ вызова гидов через команду <code>/guide</code>. Команда недоступна, если вы наиграли более 10 часов.</li><li>Изменён диалог с Путеводителем - теперь при первой встрече она говорит новому котику прописать команду <code>/guide</code>, а также скачать сборку модов</li><li>Новичкам выдаётся маленький кит - мини-вики, каменная кирка, 8 хлеба и кровать <ul><li>Все игроки могут получить такой кит раз в сутки три раза - <code>/kit newbie</code></li></ul></li><li>Табличке-куну добавлены новые реплики и советы</li><li>В Котопедиях теперь можно увидеть прогресс изучения напитков и рыбок!</li></ul><h1 id="изменено" tabindex="-1">Изменено <a class="header-anchor" href="#изменено" aria-label="Permalink to &quot;Изменено&quot;">​</a></h1><ul><li>Изменения в бартерах - некоторые удалены, некоторые изменены</li><li>Слегка изменено форматирование некоторых плагинов</li><li>Текстура луны теперь в виде кошечьки, прям как солнце | Спасибо, Master!</li><li>Рыбопедия отсортирована от 1 звезды до 5 звёзд | Спасибо, Master!</li><li>В титрах энда добавлены новые ники тех, кто помогал</li><li>Добавлено больше флагов в Защитные территории</li><li>Изменено описание защитных блоков - теперь там написано про радиус</li><li>КЭСПЭСЭПЭСЭП обновлён до последней версии</li><li>Изменена команда <code>/help</code></li><li>Из ТАБа удалена строчка про обход кхе-кхе в кхе-кхе</li><li>Табличке-куну добавлен КД на установку</li><li>Новичкам снова включено ПвП</li></ul><h1 id="баг-фикс" tabindex="-1">Баг-фикс <a class="header-anchor" href="#баг-фикс" aria-label="Permalink to &quot;Баг-фикс&quot;">​</a></h1><ul><li>Пофикшен баг 3-сезонной давности, когда плейсхолдеры [pos], [item] и прочие не отображались в личных сообщениях.</li></ul><p><img src="/assets/updates/6_2_5/bug.png" alt="Пофикшенный баг" loading="lazy"></p><ul><li>Пофикшены нерабочие команды <code>/seen</code> и <code>/findnick</code></li><li>Поле пшеницы на спавне снова можно ломать</li><li>Пофикшен баг, когда листик новичка не пропадал после 10 наигранных часов</li><li>Пофикшено сообщение “Всекоты вернули тебе часть вещей”</li><li>Пофикшен баг с нажатием кнопки на принятие запроса у гидов</li><li>В списке серверов у Кошкокрафта снова есть иконка</li><li>Выполнение достижения [Я легенда? Я легенда?] теперь пишется в чат</li><li>Плагин на пластинки обновлён, ютуб снова должен работать</li><li>Муты и размуты снова показываются в Информаторе</li><li>Шото там выключил, что РКН блокировал на сайте, поэтому щас они должны работать хорошо</li><li>Бармену добавлены звуки разговора во все его реплики</li><li>Неправильный набор брони за 300 часов пофикшен. Замену можно получить в <code>#запросы</code></li><li>Херобрин удалён</li><li>Прочие, менее важные фиксы</li></ul>',27)]))}const _=i(o,[["render",r]]);export{p as __pageData,_ as default};
